import React from 'react';
import 'antd/dist/antd.css';
import {
  PageHeader, Menu, Dropdown, Row, Col, DatePicker, Table,
} from 'antd';
import { UserOutlined, DownOutlined } from '@ant-design/icons';

const Dashboard = () => {
  const Role = localStorage.getItem('Role');
  console.log('role', Role);

  const columns = [
    {
      title: 'Process Area',
      dataIndex: 'process_area',
      key: 'process_area',
    },
    {
      title: 'Month 1',
      dataIndex: 'month_1',
      key: 'month_1',
    },
    {
      title: 'Month 2',
      dataIndex: 'month_2',
      key: 'month_2',
    },
    {
      title: 'Sum Count',
      dataIndex: 'sum_count',
      key: 'sum_count',
    },
    {
      title: 'Sum Percentage',
      dataIndex: 'sum_per',
      key: 'sum_per',
    },
  ];

  const data = [];

  for (let i = 1; i < 4; i++) {
    data.push({
      process_area: '',
      month_1: '',
      month_2: '',
      sum_count: '',
      sum_per: '',
    });
