    <Menu>
      <Menu.Item key="1" icon={<UserOutlined />}>
        Project 1
      </Menu.Item>
      <Menu.Item key="2" icon={<UserOutlined />}>
        Project 2
      </Menu.Item>
      <Menu.Item key="3" icon={<UserOutlined />}>
        Project 3
      </Menu.Item>
    </Menu>
